    private void btnCalculatePriceActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalculatePriceActionPerformed
        try {
            double price = Double.parseDouble(txtpriceField.getText());
            int amount = Integer.parseInt(txtAmount.getText());
            double totalPrice = price * amount;

            lblTotalPrice.setText(String.format("%.2f", totalPrice).replace(".", ","));
        } catch (NumberFormatException e) {
            javax.swing.JOptionPane.showMessageDialog(this, "Por favor, ingrese valores numéricos válidos.");
        }    }//GEN-LAST:event_btnCalculatePriceActionPerformed

    private void btnCalculateWeightActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalculateWeightActionPerformed
        try {
            double weight = Double.parseDouble(txtweightField.getText());
            int amount = Integer.parseInt(txtAmount.getText());
            double approximatedWeight = weight * amount;

            lblApxWght.setText(String.format("%.2f", approximatedWeight));
        } catch (NumberFormatException e) {
            javax.swing.JOptionPane.showMessageDialog(this, "Por favor, ingrese valores numéricos válidos.");
        }    }//GEN-LAST:event_btnCalculateWeightActionPerformed

    private void btnClearActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnClearActionPerformed
        txtnameField.setText("");
        txtpriceField.setText("");
        txtweightField.setText("");
        txtAmount.setText("");
        lblTotalPrice.setText("");
        lblApxWght.setText("");
    }//GEN-LAST:event_btnClearActionPerformed

    private void btnAddActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAddActionPerformed
        int id = Integer.parseInt(txtId.getText());
        String name = txtnameField.getText();
        try {
            double price = Double.parseDouble(txtpriceField.getText());
            double weight = Double.parseDouble(txtweightField.getText());
            int amount = Integer.parseInt(txtAmount.getText());

            double totalPrice = price * amount;
            double approximatedWeight = weight * amount;

            ToCloud.uploadKeyboardData(id, name, price, weight, amount, totalPrice, approximatedWeight);

            JOptionPane.showMessageDialog(this, "Keyboard data saved successfully!");

            btnClearActionPerformed(evt);

        } catch (NumberFormatException e) {
            JOptionPane.showMessageDialog(this, "Please enter valid numbers for price, weight, and amount.", "Invalid Input", JOptionPane.ERROR_MESSAGE);
        }
    }//GEN-LAST:event_btnAddActionPerformed

