    private void btnAddActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAddActionPerformed
        String name = txtNameField.getText();
        String capital = txtCapitalField.getText();
        try {
            long population = Long.parseLong(txtPopulationField.getText());
            double area = Double.parseDouble(txtAreaField.getText());
            double density = population / area;

            ToCloud.uploadCountryData(name, capital, population, area, density);

            JOptionPane.showMessageDialog(this, "Country data saved successfully!");
            btnClearActionPerformed(evt);

        } catch (NumberFormatException e) {
            JOptionPane.showMessageDialog(this, "Please enter valid numbers for population and area.", "Invalid Input", JOptionPane.ERROR_MESSAGE);
        }
    }//GEN-LAST:event_btnAddActionPerformed

    private void btnClearActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnClearActionPerformed
        txtNameField.setText("");
        txtCapitalField.setText("");
        txtAreaField.setText("");
        txtPopulationField.setText("");
        lblDensityResult.setText("");
    }//GEN-LAST:event_btnClearActionPerformed

    private void btnCalculateDensityActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalculateDensityActionPerformed
        try {
            long population = Long.parseLong(txtPopulationField.getText());
            double area = Double.parseDouble(txtAreaField.getText());
            double density = population / area;

            lblDensityResult.setText(String.format("%.2f", density));
        } catch (NumberFormatException e) {
            JOptionPane.showMessageDialog(this, "Please enter valid numbers for population and area.", "Invalid Input", JOptionPane.ERROR_MESSAGE);
        }
    }//GEN-LAST:event_btnCalculateDensityActionPerformed

