    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
    String input = jTextField1.getText();
    if (input.matches("[0-9,\\s]+")) {
        System.out.println("Valid entry: " + input);
    } else {    
        javax.swing.JOptionPane.showMessageDialog(this, "Please enter only numbers and commas.");
        jTextField1.setText("");
    }
    SortController controller = new SortController();
    String sortedOutput = controller.processInput(input);
    jTextField2.setText(sortedOutput);
    
    }//GEN-LAST:event_jButton1ActionPerformed

